import React, { useState, useEffect, useRef } from 'react';
import './index.css';

// API endpoint configuration
const API_BASE_URL = 'http://localhost:5000';

function SimpleApp() {
  const [isRecording, setIsRecording] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [messages, setMessages] = useState([
    { sender: 'System', text: 'Welcome to the Voice Agent demo. Click "Start Recording" to begin.' }
  ]);
  const [audioStream, setAudioStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [apiStatus, setApiStatus] = useState('unknown');
  const [recordingTime, setRecordingTime] = useState(0);
  const [volume, setVolume] = useState(0);
  const timerRef = useRef(null);
  const animationRef = useRef(null);
  const audioAnalyserRef = useRef(null);
  const audioDataRef = useRef(null);
  const messagesEndRef = useRef(null);

  // Check API connection on mount
  useEffect(() => {
    checkApiConnection();
    return () => {
      // Clean up on unmount
      if (timerRef.current) clearInterval(timerRef.current);
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
      if (audioStream) {
        audioStream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  // Auto-scroll to the bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Check if the API server is running
  const checkApiConnection = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        setApiStatus('connected');
        addMessage('System', 'Connected to API server successfully.');
      } else {
        setApiStatus('error');
        addMessage('System', 'Error connecting to API server. Check if it\'s running.');
      }
    } catch (err) {
      console.error('API connection error:', err);
      setApiStatus('error');
      addMessage('System', `Error connecting to API server: ${err.message}. Make sure the server is running at ${API_BASE_URL}`);
    }
  };

  const startRecording = async () => {
    try {
      // Clear previous audio chunks
      setAudioChunks([]);

      // Request access to the microphone
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setAudioStream(stream);

      // Set up audio analysis for visualization
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const analyser = audioContext.createAnalyser();
      analyser.fftSize = 256;
      const audioSource = audioContext.createMediaStreamSource(stream);
      audioSource.connect(analyser);

      // Store the analyser for later use
      audioAnalyserRef.current = analyser;
      audioDataRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Start visualization
      visualizeAudio();

      // Create a new MediaRecorder instance
      const recorder = new MediaRecorder(stream, { mimeType: 'audio/webm' });
      setMediaRecorder(recorder);

      // Event handler for when data is available
      recorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          setAudioChunks(chunks => [...chunks, e.data]);
        }
      };

      // Start recording
      recorder.start(200); // Collect data every 200ms
      setIsRecording(true);
      addMessage('System', 'Recording started. Speak now...');

      // Start timer
      setRecordingTime(0);
      timerRef.current = setInterval(() => {
        setRecordingTime(prevTime => prevTime + 1);
      }, 1000);

    } catch (err) {
      console.error('Error starting recording:', err);
      addMessage('System', `Error starting recording: ${err.message}`);
    }
  };

  // Visualize audio input
  const visualizeAudio = () => {
    if (!audioAnalyserRef.current) return;

    const analyser = audioAnalyserRef.current;
    const dataArray = audioDataRef.current;

    // Get volume data
    analyser.getByteFrequencyData(dataArray);

    // Calculate volume level (average of frequency data)
    const average = dataArray.reduce((acc, val) => acc + val, 0) / dataArray.length;
    setVolume(average);

    // Continue animation
    animationRef.current = requestAnimationFrame(visualizeAudio);
  };

  const stopRecording = () => {
    if (mediaRecorder && isRecording) {
      // Stop the recording
      mediaRecorder.stop();
      setIsRecording(false);
      addMessage('System', 'Recording stopped. Processing audio...');

      // Stop the timer
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }

      // Stop the visualization
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }

      // Stop all tracks in the stream
      if (audioStream) {
        audioStream.getTracks().forEach(track => track.stop());
        setAudioStream(null);
      }

      // Process the recorded audio after a short delay to ensure all chunks are collected
      setTimeout(() => {
        if (audioChunks.length > 0) {
          const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
          processAudio(audioBlob);
        } else {
          addMessage('System', 'No audio recorded. Please try again.');
        }
      }, 300);
    }
  };

  const processAudio = async (audioBlob) => {
    try {
      setIsProcessing(true);
      addMessage('System', 'Processing audio...');

      // Create a FormData object to send the audio file
      const formData = new FormData();
      formData.append('audio', audioBlob);

      // Send the audio to the backend for processing
      const response = await fetch(`${API_BASE_URL}/api/process-audio`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        let errorMessage = 'Failed to process audio';
        try {
          const errorData = JSON.parse(errorText);
          errorMessage = errorData.detail || errorMessage;
        } catch (e) {
          // If parsing fails, use the raw text
          errorMessage = errorText || errorMessage;
        }
        throw new Error(errorMessage);
      }

      const data = await response.json();

      // Add user message with transcribed text
      addMessage('User', data.user_text);

      // Add agent response
      addMessage('Agent', data.response_text);

      // Play the audio response
      if (data.audio_id) {
        const audioUrl = `${API_BASE_URL}/api/audio/${data.audio_id}`;
        playAudioFromUrl(audioUrl);
      }

    } catch (err) {
      console.error('Error processing audio:', err);
      addMessage('System', `Error processing audio: ${err.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  const playAudioFromUrl = (url) => {
    // Create an audio element to play the response
    const audio = new Audio(url);

    // Add event listeners
    audio.addEventListener('play', () => {
      addMessage('System', 'Playing audio response...');
    });

    audio.addEventListener('ended', () => {
      addMessage('System', 'Audio response finished.');
    });

    audio.addEventListener('error', (e) => {
      console.error('Error playing audio:', e);
      addMessage('System', 'Error playing audio response.');
    });

    // Play the audio
    audio.play().catch(err => {
      console.error('Error playing audio:', err);
      addMessage('System', `Error playing audio: ${err.message}`);
    });
  };

  const addMessage = (sender, text) => {
    setMessages(prev => [...prev, { sender, text, timestamp: new Date() }]);
  };

  return (
    <div className="container">
      <div className="header">
        <h1 className="title">Voice Agent Demo</h1>
        <h2 className="subtitle">Talk with an AI voice assistant</h2>
      </div>

      <div className="card">
        <div className="controls">
          {!isRecording ? (
            <button 
              className="button" 
              onClick={startRecording}
            >
              Start Recording
            </button>
          ) : (
            <button 
              className="button" 
              onClick={stopRecording}
            >
              Stop Recording
            </button>
          )}
        </div>

        <div className={`status ${isRecording ? 'connected' : 'disconnected'}`}>
          Status: {isRecording ? 'Recording' : 'Not Recording'}
        </div>
      </div>

      <div className="card">
        <h3>Conversation</h3>
        <div className="transcript">
          {messages.map((msg, idx) => (
            <div 
              key={idx} 
              className={`message ${msg.sender.toLowerCase() === 'user' ? 'user' : 
                           msg.sender.toLowerCase() === 'agent' ? 'agent' : ''}`}
            >
              <div className="message-sender">{msg.sender}</div>
              <p className="message-text">{msg.text}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SimpleApp;
